def find_most_frequent_word(text):
    """Find most common word in the text."""

    words = text.split()

    frequency = {}
    for word in words:
        if word in frequency:
            frequency[word] += 1
        else:
            frequency[word] = 1

    max_frequency = 0
    most_common_word = None

    for word, count in frequency:
        if count > max_frequency:
            max_frequency = count
            most_common_word = word

    return most_common_word
